const Home = ({ covidData }) => {
    return (


        <div className="body">
            <img className="card__photo" width="100px" src={covidData.countryInfo.flag} />
            <div className="main">
                <h1>{covidData.country}</h1>
                <p>Cases: {covidData.cases}</p>
                <p>Deaths: {covidData.deaths}</p>
                <p>Recovered:üëåüèø {covidData.recovered}</p>
                <p>Active: {covidData.active}</p>
            </div>

            <style jsx>{`


                .body{
                    margin:0 auto;
                    display:flex;
                    flex-direction: column;
                    width:150px;
                    height:200px;
                    background-color:#f1f1f1;
                    font-family: Nunito
                    font-size: 11px;
                } 
                .card__photo{
                    width:30px;
                    height:30px;
                    border-radius: 50%;
                    margin-left: 35%;
                    margin-top: -10px;
                    position: relative
                }
                .main {
                    margin:0 auto;
                }
        `}</style>
        </div>
    );

}

export async function getStaticProps(context) {
    const reply = await fetch("https://corona.lmao.ninja/v2/countries/gh");

    return {
        props: {
            covidData: await reply.json(),
        },
    };
}

export default Home;
